// Navbar.js
import React, { useState } from 'react';
import LoginModal from '../Authentication/Login';
import SignupModal from '../Authentication/Signup';

const Navbar = () => {

  const [modalOpen, setModalOpen] = useState(false);
  const [modalSignupOpen, setSignupModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const openSignupModal = () => {
    setSignupModalOpen(true);
  };

  const closeSignupModal = () => {
    setSignupModalOpen(false);
  };

  return (
    <nav className="bg-blue-500 p-4">
      <div className="container mx-auto">
        <div className="flex justify-between items-center">
          <div className="text-white text-2xl font-bold font-custom">My ShoperMart</div>
          <ul className="flex space-x-4">
            <li>
              <a href="#" className="text-white hover:text-gray-200">Home</a>
            </li>
            <li>
              <a href="#" className="text-white hover:text-gray-200">Products</a>
            </li>
            <li>
              <a href="#" className="text-white hover:text-gray-200">About</a>
            </li>
            <li>
              <a href="#" className="text-white hover:text-gray-200">Contact</a>
            </li>
            <li>
              <a href="#" onClick={modalOpen} className="text-white hover:text-gray-200">Login</a>
            </li>
          </ul>
          <LoginModal isOpen={modalOpen} onClose={closeModal} />
          <SignupModal isOpen={modalSignupOpen} onClose={closeSignupModal} />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
